import React, { useState, useEffect } from 'react'
import { Line } from 'react-chartjs-2'
import axios from 'axios';

function LineChart() {

    const [chartData, setChartData] = useState([]);
    const [playerSalary, setPlayerSalary] = useState([]);
    const [playerName, setPlayerName] = useState([]);

    const chart = () => {
    axios
    .get('https://gist.githubusercontent.com/haikafaeq/cfd8369fa57f29e60e1db2dc1edff819/raw/a69d1d7fb594bc3a7386d0c9cd1e73e8457beb02/lineChart.json')
    .then(res => {
        console.log(res);
        setEmployeeSalary(res.data);
        setEmployeeAge(res.data);
        for(const dataObj of res.data.Employee) {
            employeeSalary.push(parseInt(dataObj.employee_salary));
            employeeAge.push(parseInt(dataObj.employee_age));
        }
    setChartData({
        labels: employeeAge,
        datasets: [
            {
            label: "level of Player Salary",
            data: employeeSalary,
            backgroundColor: ["rgba(75, 192, 192, 0.6)"],
            borderWidth: 4
            }
            ]
        });
      })
      .catch(err => {
        console.log(err);
    });
    console.log(employeeSalary, employeeAge)   
};

useEffect(() => { 
chart();
}, []);

return (
<div className="App">
  <h1>Test Chart using API</h1>
  <div style={{height: "500px", width: "500px"}} >
    <Line
      data={chartData}
      options={{
        responsive: true,
        title: { text: "THICCNESS SCALE", display: true },
        scales: {
          yAxes: [
            {
              ticks: {
                autoSkip: true,
                maxTicksLimit: 10,
                beginAtZero: true,
                stepSize: 1
              },
              gridLines: {
                display: false
              }
            }
          ],
          xAxes: [
            {
              gridLines: {
                display: false
              }
            }
          ]
        }
      }}
    />
  </div>
</div>
);
};


export default LineChart
