import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import axios from 'axios';

const BarChart = () => {
  const [chartData, setChartData] = useState([]);
  const [item, setItem] = useState([]);
  
    const chart = () => {
        axios
        .get("https://gist.githubusercontent.com/haikafaeq/8ef0f14d8e0f04d96f70ccbe3bd615ad/raw/cffc31a83f14422ca38788191247fbdc10e8af41/try.json")    
        .then(res => {
            console.log(res);
            setItem(res.data);
            for(const dataObj of res.data.data) {
                item.push(dataObj);
            }
        setChartData({
            labels:  ['Jan','Feb','Mar','Apr','May'],
            datasets: [
                {
                label: "level of data",
                data: item,
                backgroundColor: ["rgba(75, 192, 192, 0.6)"],
                borderWidth: 4
                }
                ]
            });
          })
          .catch(err => {
            console.log(err);
        });
        console.log(item)   
    };

  useEffect(() => { 
    chart();
  }, []);

  return (
    <div className="App">
      <h1>Test Chart using API</h1>
      <div style={{height: "500px", width: "500px"}} >
        <Bar
          data={chartData}
          options={{
            responsive: true,
            title: { text: "BAR SCALE", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true
                  },
                  gridLines: {
                    display: false
                  }
                }
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false
                  }
                }
              ]
            }
          }}
        />
      </div>
    </div>
  );
};

export default BarChart;
