import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";

const TestChart = () => {
  const [chartData, setChartData] = useState({});
  const [employeeSalary, setEmployeeSalary] = useState([]);
  const [employeeAge, setEmployeeAge] = useState([]);

  const chart = () => {
    axios
      .get("https://gist.githubusercontent.com/haikafaeq/01db9b03b5d69aff742bb33e558f70d2/raw/b1b90cba7015d63eb1702681e1a7667ea76ec17a/testChart.json")
      .then(res => {
        console.log(res);
        setEmployeeSalary(res.data)
        setEmployeeAge(res.data)
        for (const dataObj of res.data.data) {
            employeeSalary.push(parseInt(dataObj.employee_salary));
            employeeAge.push(parseInt(dataObj.employee_age));
        }
        setChartData({
          labels: employeeAge,
          datasets: [
            {
              label: "level of thiccness",
              data: employeeSalary,
              backgroundColor: ["rgba(75, 192, 192, 0.6)"],
              borderWidth: 4
            }
          ]
        });
      })
      .catch(err => {
        console.log(err);
      });
    console.log(employeeSalary, employeeAge);
  };

  useEffect(() => {
    chart();
  }, []);

  return (
    <div className="App">
      <h1>TestChart</h1>
      <div>
        <Line
          data={chartData}
          options={{
            responsive: true,
            title: { text: "THICCNESS SCALE", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true
                  },
                  gridLines: {
                    display: false
                  }
                }
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false
                  }
                }
              ]
            }
          }}
        />
      </div>
    </div>
  );
};

export default TestChart;
