import React, {useState, useEffect} from 'react'
import { Bar } from 'react-chartjs-2'
import axios from 'axios'

function BarChart() {

    
    const [item, setItem] = useState([]);

    const chart = () => {
        axios
        .get('https://gist.githubusercontent.com/haikafaeq/8ef0f14d8e0f04d96f70ccbe3bd615ad/raw/cffc31a83f14422ca38788191247fbdc10e8af41/try.json')
        .then(res => {
            console.log(res);
            setItem(res.data);
            for(const dataObj of res.data.data) {
                item.push(dataObj);
            }
            setChartData({
                labels: [ 'Jan','Feb','Mar','Apr','May'],
                datasets: [
                    {
                        label: "Sales for 2020 (M)",
                        data: item,
                        borderColor: [
                        'rgba(255, 206, 86, 0.2',
