                        'rgba(255, 206, 86, 0.2',
                        'rgba(255, 206, 86, 0.2',
                        'rgba(255, 206, 86, 0.2'
                    ],
                        backgroundColor: [
                        'rgba(255, 206, 86, 0.2',
                        'rgba(255, 206, 86, 0.2',
                        'rgba(255, 206, 86, 0.2',
                        'rgba(255, 206, 86, 0.2',
                        'rgba(255, 206, 86, 0.2'
                    ]
                       
                    },
                    {
                        label: 'Sales for 2019 (M)',
                        data: [1, 3, 2, 2, 3],
                        borderColor: [
                        'rgba(54, 162, 235, 0.2',
                        'rgba(54, 162, 235, 0.2',
                        'rgba(54, 162, 235, 0.2',
                        'rgba(54, 162, 235, 0.2',
                        'rgba(54, 162, 235, 0.2'
                    ],
                        backgroundColor: [
                        'rgba(54, 162, 235, 0.2',
                        'rgba(54, 162, 235, 0.2',
                        'rgba(54, 162, 235, 0.2',
                        'rgba(54, 162, 235, 0.2',
                        'rgba(54, 162, 235, 0.2'
                    ]
                }    
                    
                ]
                });
        })
        .catch(err => {
            console.log(err)
        })
        console.log(item)
    };
        useEffect(() => { 
            chart();
          }, []);

   

        const options = {
            title: {
                display: true,
                text: 'Bar Chart'
            },
            scales: {
                yAxes: [
                    {
                    ticks: {
                        min: 0,
                        max: 6,
                        stepSize: 1
                    }
                }
                ]
            }
        }

