import React from 'react'
import { Bar } from 'react-chartjs-2'

function BarChart() {

    const [item, setItems] = useState([]);

    useEffect(() => {
        axios
        .get('https://gist.githubusercontent.com/haikafaeq/07b106d20c9f5ce4daa6057a9858796a/raw/2f9e7c7d0f82700f239f55ea784be5ec0550219a/datafetching.json')
        .then(res => {
            console.log(res)
            setItems(res.data)
        })
        .catch(err => {
            console.log(err)
        })

    }
    )

    const data = {
        labels: [ 'Jan','Feb','Mar','Apr','May'],
        datasets: [
            {
                label: "Sales for 2020 (M)",
                data: <p key={item.id[1]}>{item.num} </p>,
                borderColor: [
                'rgba(255, 206, 86, 0.2',
                'rgba(255, 206, 86, 0.2',
                'rgba(255, 206, 86, 0.2',
                'rgba(255, 206, 86, 0.2',
                'rgba(255, 206, 86, 0.2'
            ],
                backgroundColor: [
                'rgba(255, 206, 86, 0.2',
                'rgba(255, 206, 86, 0.2',
                'rgba(255, 206, 86, 0.2',
                'rgba(255, 206, 86, 0.2',
                'rgba(255, 206, 86, 0.2'
            ]
               
            },
            {
                label: 'Sales for 2019 (M)',
                data: [1, 3, 2, 2, 3],
                borderColor: [
                'rgba(54, 162, 235, 0.2',
                'rgba(54, 162, 235, 0.2',
                'rgba(54, 162, 235, 0.2',
                'rgba(54, 162, 235, 0.2',
                'rgba(54, 162, 235, 0.2'
            ],
                backgroundColor: [
                'rgba(54, 162, 235, 0.2',
                'rgba(54, 162, 235, 0.2',
                'rgba(54, 162, 235, 0.2',
                'rgba(54, 162, 235, 0.2',
                'rgba(54, 162, 235, 0.2'
            ]
               
           
            }
        ]
        }

        const options = {
            title: {
                display: true,
                text: 'Bar Chart'
            },
            scales: {
                yAxes: [
                    {
                    ticks: {
                        min: 0,
                        max: 6,
                        stepSize: 1
                    }
                }
                ]
            }
        }

        return <Bar data={data} options={options} />
}


export default BarChart
